import React, { useState } from "react";
import { Navbar } from "../../components/Navbar/Navbar";
import "./Search.scss";
import axios from "axios";
import { APIKey } from "../../constants/constants";
import { useDispatch } from "react-redux";
import { apiTransform } from "../../api/apiTransform";
import { setSearchResults } from "../../store/videosSlice";

export const Search: React.FC = () => {
  const [searchRequest, setSearchRequest] = useState("");
  const dispatch = useDispatch();

  const onSearchClick = () => {
    axios
      .get(
        `https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=12&q=${searchRequest}&key=${APIKey}`
      )
      .then((response) => dispatch(setSearchResults(apiTransform(response))));
  };

  return (
    <div className="searchPageContainer">
      <Navbar />
      <div className="searchContainer">
        <div className="searchTitle">Search for a video</div>
        <div className="searchForm">
          <input
            type="text"
            placeholder="What are you looking for?"
            onChange={(event) => setSearchRequest(event.target.value)}
          />
          <button onClick={onSearchClick}>Search</button>
        </div>
      </div>
    </div>
  );
};
