import React, { Dispatch, SetStateAction, useState } from "react";
import "./Modal.scss";

interface ModalProps {
  setIsFavoritesModalActive: Dispatch<SetStateAction<boolean>>;
  searchRequest: string;
}

export const Modal: React.FC<ModalProps> = (props) => {
  const favorites = JSON.parse(localStorage.getItem("favorites")!);
  const [numberOfResults, setNumberOfResults] = useState(25);

  const onSaveClick = () => {
    localStorage.setItem(
      "favorites",
      JSON.stringify([...favorites, props.searchRequest])
    );

    props.setIsFavoritesModalActive(false);
  };

  return (
    <div className="modalContainer">
      <div className="modalTitle">Save a search request</div>

      <div className="modalGroup">
        <div className="modalInputContainer">
          <div className="modalTextField">
            <label>Request</label>
            <input type="text" value="request" disabled />
          </div>
        </div>
        <div className="modalInputContainer">
          <div className="modalTextField">
            <label>Name</label>
            <input type="text" placeholder="Enter the name" />
          </div>
        </div>
        <div className="modalInputContainer">
          <div className="modalTextField">
            <label>Sort by</label>
            <select>
              <option>Unsorted</option>
              <option>Video count</option>
              <option>Title</option>
              <option>Relevance</option>
              <option>Rating</option>
            </select>
          </div>
        </div>

        <div className="sliderInfo">
          <div className="title">Max count</div>
          <div className="sliderParameter">
            <div className="sliderContainer">
              <input
                type="range"
                min="1"
                max="50"
                step="1"
                defaultValue="25"
                className="slider"
                onChange={(event) => {
                  setNumberOfResults(event.target.valueAsNumber);
                }}
              />
            </div>
            <input className="numberOfResults" value={numberOfResults} />
          </div>

          <div className="modalButtonsGroup">
            <div className="modalButtonWhite">
              <button onClick={() => props.setIsFavoritesModalActive(false)}>
                Don't save
              </button>
            </div>
            <div className="modalButtonBlue">
              <button>Save</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
